/**
 * @brief   Low level HAL driver initialization.
 *
 * @notapi
 */
void hal_lld_init(void)
{
  /* Update SystemCoreClock */
  SystemCoreClockUpdate();

  /* SysTick initialization using the system clock.*/
  nvicSetSystemHandlerPriority(HANDLER_SYSTICK, CORTEX_PRIORITY_SYSTICK);
  SysTick->LOAD = SystemCoreClock / CH_FREQUENCY - 1;
  SysTick->VAL = 0;
  SysTick->CTRL = SysTick_CTRL_CLKSOURCE_Msk |
                  SysTick_CTRL_ENABLE_Msk |
                  SysTick_CTRL_TICKINT_Msk;
}

/**
 * @brief   AT91SAM3N clocks and PLL initialization.
 * @note    All the involved constants come from the file @p board.h.
 * @note    This function must be invoked only after the system reset.
 *
 * @special
 */
 
#if defined(SAM3N_PLATFORM) || defined(SAM3S_PLATFORM) || defined(SAM3U_PLATFORM)
/*
 * Clocks initialization for the SAM3N family.
 */
void at91sam3_clock_init(void)
{
   SystemInit();
}
#endif

